/**
 * Create a lookup map for faster case-insensitive matching
 */
const createLookupMap = () => {
    const map = new Map();
    for (const [key, translations] of Object.entries(BUSINESS_GLOSSARY)) {
        map.set(key.toLowerCase(), { original: key, translations });
    }
    return map;
};

export const GLOSSARY_LOOKUP = createLookupMap();

/**
 * Get glossary translation for a term
 * @param {string} text - The English text to translate
 * @param {string} locale - Target locale
 * @returns {string|null} - Translation or null if not in glossary
 */
export const getGlossaryTranslation = (text, locale) => {
    if (!text || locale === 'en') return null;
    
    const trimmed = text.trim();
    const entry = GLOSSARY_LOOKUP.get(trimmed.toLowerCase());
    
    if (entry && entry.translations[locale]) {
        // Preserve original casing style
        const translation = entry.translations[locale];
        
        // If original was all uppercase, make translation uppercase too
        if (trimmed === trimmed.toUpperCase() && trimmed.length > 1) {
            return translation.toUpperCase();
        }
        
        return translation;
    }
    
    return null;
};

/**
 * Check if text is in the glossary
 */
export const isInGlossary = (text) => {
    if (!text) return false;
    return GLOSSARY_LOOKUP.has(text.trim().toLowerCase());
};

export default BUSINESS_GLOSSARY;
